*{
    list-style: none;
    margin: 0px;
    padding: 0px;
}
/* 聊天页面 */
#chat-box{
    margin: 0 auto;
    background: #f8f8f8;
    max-width: 800px;
    border: 1px solid #ddd;
    box-shadow: 0 0 5px rgba(0, 0, 0, .1);
    border-radius: 2px;
    display: flex;
}
#chat-box .left{
    width: 200px;
    border-right: 1px solid #eee;
}
#chat-box .left #user-box li{
    border: 1px solid #eee;
}
#chat-box .right {
    height: 600px;
    border-bottom: 1px solid #ddd;
    padding: 20px;
    background: #fff;
    display: flex;
    flex: 1;
    flex-direction: column;
}
#chat-box .right .top{
    flex: 1;
    display: flex;
    flex-direction: column;
    margin-bottom: 20px;
    overflow: hidden;
}
#chat-box .right .top #list-box{
    overflow-y: scroll;
}
#chat-box #list-box li{
    text-align: left;
}
#chat-box #list-box li.my{
    text-align: right;
}
#chat-box #list-box li.sys{
    text-align: center;
}
#chat-box #list-box li .name{
    font-size: 8px;
}
#chat-box #list-box li .message{
    display: inline-block;
    border: 1px solid #666;
    border-radius: 10px;
    padding: 5px 10px;
    font-size: 16px;
}
#chat-box .right .bottom{
    border-top: 1px solid #eee;
    margin: 0 -20px;
    overflow: hidden;
}
#chat-box .right .bottom .input{
    padding: 20px;
    height: 60px;
    background: #fff;  
}
#chat-box .right .bottom .input input{
    border: none;
    width: 100%;
    height: 100%;
}
#chat-box .right .bottom #message-btn{
    float: right;
}
/* 登录 */
#login-box{
    display: block;
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, .8);
}
#login-box .box{
    position: absolute;
    top: 50%;
    left: 50%;
    margin-top: -100px;
    margin-left: -200px;
    background: #fff;
    width: 400px;
    padding: 40px;
    border-radius: 10px;
}
#login-box .box .input{
    border: 1px solid #ddd;
    padding: 10px;
    margin-bottom: 20px;
    border-radius: 5px;
    overflow: hidden;
}
#login-box .box .input input{
    border: none;
    display: block;
    width: 100%;
    outline: none;
    font-size: 20px;
    line-height: 20px
}

.btn{
    width: 100px;
    background: #333;
    color: #fff;
    text-align: center;
    line-height: 40px;
    border-radius: 5px;
    transition: all .3s ease;
    cursor: pointer;
}
.btn:hover{
    background: black;
}
